#![feature(slice_partition_dedup)]
fn find_distinct_char_patch(input: &[char], patch_len: usize) -> usize {
    let mut offset = 0_usize;
    for (index, token) in input.windows(patch_len).enumerate() {
        let mut token = token.iter().map(|c| *c).collect::<Vec<_>>();
        token.sort();
        if token.partition_dedup().1.len() == 0 {
            offset = index + patch_len;
            break;
        }
    }
    offset
}

fn part1(input: &[char]) {
    println!("{}", find_distinct_char_patch(input, 4));
}

fn part2(input: &[char]) {
    println!("{}", find_distinct_char_patch(input, 14));
}

fn main() {
    let input = INPUT.chars().collect::<Vec<_>>();
    part1(&input);
    part2(&input);
}

static INPUT: &str = r#""#;
